import React from 'react'
import Board from './components/Board.jsx'
import { useState } from 'react'
import './App.css'

function App() {
  const [board, setBoard] = useState(Array(9).fill("o"));
  const [xPlaying, setXPlaying] = useState(true);

  const updateBoard = (id) => {
    setBoard( (board) => {
      const newBoard = board.map( (item, index) => {
        if (index === id)
          return xPlaying === true ? "X" : "";
        else
          return item;
      })
      return [...newBoard];
    });
  }
  return (
    <div className='App'>
      <Board board_value={board} board_onClick={updateBoard}/>
    </div>
  );
}

export default App;
