import React from 'react'
import './ScoreBoard.css'

const ScoreBoard = ({score_value, scoreBoard_xPlaying}) => {
    return (
        <div className='score_board'>
            <span className={'score x-score ${!scoreBoard_xPlaying && "inactive"}'}>X- {score_value.xScore}</span>
            <span className={'score o-score ${scoreBoard_xPlaying && "inactive"}'}>O- {score_value.oScore}</span>
        </div>
    )
}

export default ScoreBoard;
